import React, { Component } from 'react'
import { NavLink } from 'react-router-dom';
import './navbar.scss'
import '../App.scss'
import $ from 'jquery';
export class Navbars extends Component
{
    state = {
        scrolled: false
    }
    componentDidMount()
    {
        $('#icon').on('click', function ()
        {
            $('#ul').toggleClass('show');
        });
        // close hamburger menu after click a
        if ($('#ul').hasClass('show')) {
            $('.navbar__link  ').on("click", function ()
            {
                $('ul').toggleClass('show');
            });
        }


        //usage of JQuery to handle menubar on screen resize
        //and
        //React states to handle scroll sticky navbar
        window.addEventListener('scroll', this.handleScroll, true);

    }

    componentWillUnmount()
    {
        window.removeEventListener('scroll', this.handleScroll);
    }
    handleScroll = () =>
    {
        if (window.scrollY > 20) {
            this.setState({ scrolled: true });
        } else {
            this.setState({ scrolled: false });
        }
    }
    render()
    {
        return (
            <div>
                <nav className={
                    this.state.scrolled ? 'navbar sticky' : 'navbar'
                }>
                    <label className="logo">
                        <a href="/">

                            <img src="/favicon.ico"
                                width="50"
                                height="50"
                                alt="logo" />
                        </a>
                    </label>

                    <ul id="ul">
                        <li className="nav-lists">
                            <NavLink to="/"
                                exact
                                activeClassName="navbar__link--active"
                                className="navbar__link">
                                Home
                        </NavLink>
                        </li>
                        <li className="nav-lists">
                            <NavLink
                                exact
                                activeClassName="navbar__link--active"
                                className="navbar__link" to="/projects" >
                                Projects
                        </NavLink>
                        </li>
                        <li className="nav-lists">
                            <NavLink
                                exact
                                activeClassName="navbar__link--active"
                                className="navbar__link" to="/about">
                                About
                        </NavLink>
                        </li>
                    </ul>
                    <label id="icon">
                        <i className="fas fa-bars"></i>
                    </label>
                </nav>
            </div>
        )
    }
}

export default Navbars
