import React, { Component } from 'react'
import Particles from "react-particles-js";
export class Particles_component extends Component
{
    render()
    {
        return (

            <div
                style={{
                    position: "fixed",
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    width: "100vw",
                    height: "100vh"
                }}
            >
                <Particles
                    params={{
                        fpsLimit: 60,
                        particles: {
                            number: {
                                value: 0,
                                density: {
                                    enable: true,
                                    value_area: 800
                                }
                            },
                            color: {
                                value: "#ff0000",
                                animation: {
                                    enable: true,
                                    speed: 180,
                                    sync: true
                                }
                            },
                            shape: {
                                type: "circle",
                                stroke: {
                                    width: 0,
                                    color: "#000000"
                                },
                                polygon: {
                                    nb_sides: 5
                                },
                                image: {
                                    src: "https://cdn.matteobruni.it/images/particles/github.svg",
                                    width: 100,
                                    height: 100
                                }
                            },
                            opacity: {
                                value: 0.5,
                                random: false,
                                anim: {
                                    enable: false,
                                    speed: 3,
                                    opacity_min: 0.1,
                                    sync: false
                                }
                            },
                            size: {
                                value: 15,
                                random: {
                                    enable: true,
                                    minimumValue: 10
                                },
                                animation: {
                                    enable: true,
                                    speed: 7,
                                    minimumValue: 1,
                                    sync: true,
                                    startValue: "min",
                                    destroy: "max"
                                }
                            },
                            links: {
                                enable: false
                            },
                            move: {
                                enable: true,
                                speed: 2,
                                direction: "right",
                                distance: 10,
                                random: true,
                                straight: false,
                                outMode: "destroy",
                                attract: {
                                    enable: false,
                                    rotateX: 600,
                                    rotateY: 1200
                                }
                            }
                        },
                        interactivity: {
                            detectsOn: "window",
                            events: {
                                onhover: {
                                    enable: true,
                                    mode: "trail"
                                },
                                onclick: {
                                    enable: true,
                                    mode: "push"
                                },
                                onDiv: {
                                    enable: true,
                                    selectors: ["text - container"],
                                },
                                resize: true
                            },
                            modes: {
                                grab: {
                                    distance: 400,
                                    line_linked: {
                                        opacity: 1
                                    }
                                },
                                bubble: {
                                    distance: 400,
                                    size: 40,
                                    duration: 2,
                                    opacity: 0.8,
                                    speed: 3
                                },
                                repulse: {
                                    distance: 200
                                },
                                push: {
                                    particles_nb: 4
                                },
                                remove: {
                                    particles_nb: 4
                                },
                                trail: {
                                    delay: 0.05,
                                    quantity: 5
                                }
                            }
                        },
                        retina_detect: true,
                        background: {
                            color: "#000000",
                            image: "",
                            position: "50% 50%",
                            repeat: "no-repeat",
                            size: "cover"
                        }
                    }}
                />

            </div>
        )
    }
}

export default Particles_component
