import React, { Component } from 'react'
import './App.scss';
import { BrowserRouter, Route, Switch } from "react-router-dom";
import Project from './components/Project';
import About from './components/About';
import Home from './components/Home';
import Footer from './components/Footer';
import Navbars from './components/Navbars';
import Particles_component from './components/Particles_component'
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
import Loader from "react-loader-spinner";
export class App extends Component
{
  constructor()
  {
    super();
    this.state = { isLoading: true, mouseMoved: false }
  }
  componentDidMount()
  {
    setTimeout(function ()
    { //Start the timer
      this.setState({ isLoading: false }) //After 1 second, set render to true
    }.bind(this), 1000)

    document.addEventListener('mousemove', (e) =>
    {
      this.setState({ mouseMoved: true })
      co
    })

  }
  render()
  {
    return (

      this.state.isLoading ?
        <Loader type="Grid" color="#f9fc2d" height={80} width={80}
          style={{
            top: 0,
            left: 0,
            bottom: 0,
            right: 0,
            position: 'fixed',
            height: '2em',
            width: '2em',
            overflow: 'show',
            margin: 'auto',
          }} /> :
        <div className="App">
          <BrowserRouter>
            <div
              style={{
                position: "absolute",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%"
              }}
            >
              {this.state.mouseMoved ? <Particles_component /> : ""}
              <div
                style={{
                  position: "absolute",
                  top: 0,
                  left: 0,
                  width: "100%",
                  height: "100%"
                }}
              >
                <Navbars />
                <Switch>
                  <Route exact path="/">
                    <Home />
                  </Route>
                  <Route exact path="/projects">
                    <Project />
                  </Route>
                  <Route path="/about">
                    <About />
                  </Route>
                </Switch>
                <Footer />

              </div>
            </div>
          </BrowserRouter>
        </div>

    )
  }
}

export default App
