import React, { useState } from 'react'
import { Button, Image } from 'react-bootstrap'
import { Document, Page, pdfjs } from "react-pdf";
import './resume.css'
import Navbars from './Navbars'
const Resume = () =>
{
    pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

    const [numPages, setNumPages] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);

    function onDocumentLoadSuccess({ numPages })
    {
        setNumPages(numPages);
    }

    return (
        <div>
            <Navbars />
            <div className="container-resume">
                <div className="items pdf">
                    <Document
                        file="/Harini_CV.pdf"
                        className="document"
                    >
                        <div>
                            <Page
                                pageNumber={pageNumber}
                                height={600}
                                width={900}
                                className="page"
                            />
                        </div>
                    </Document>
                </div>
                <div className="items download" style={{ alignSelf: "flex-start" }}>

                    <a href="/Harini_CV.pdf" download="HariniNatarajan_Resume.pdf"><Button variant="outline-success" >
                        Download Resume
                        <Image src="/icons/download.svg" alt="download icon" width="60px" height="50px" />
                    </Button></a>

                </div>
            </div>
            <div className="p-resume">
                <p>Page {pageNumber} of {numPages}</p>
            </div>

        </div >
    )
}

export default Resume
